declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);

set @sql_instance = '$server';
set @perfmon_host_name = '$perfmon_host_name';
set @params = N'@perfmon_host_name varchar(255)';

set quoted_identifier off;
set @sql = "select sql_memory_model_desc from sys.dm_os_sys_info as osi;"
set quoted_identifier on;

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql , @params, @perfmon_host_name;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql , @params, @perfmon_host_name;

declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);

set @sql_instance = '$server';
set @perfmon_host_name = '$perfmon_host_name';
set @params = N'@perfmon_host_name varchar(255)';

set quoted_identifier off;
set @sql = "
select os_uptime = Concat
        (
            RIGHT('000'+CAST(ISNULL((datediff(second,dateadd(SECOND,-osi.ms_ticks/1000,GETDATE()),GETDATE()) / 3600 / 24), 0) AS VARCHAR(3)),3)
            ,' '
            ,RIGHT('00'+CAST(ISNULL(datediff(second,dateadd(SECOND,-osi.ms_ticks/1000,GETDATE()),GETDATE()) / 3600  % 24, 0) AS VARCHAR(2)),2)
            ,':'
            ,RIGHT('00'+CAST(ISNULL(datediff(second,dateadd(SECOND,-osi.ms_ticks/1000,GETDATE()),GETDATE()) / 60 % 60, 0) AS VARCHAR(2)),2)
            ,':'
            ,RIGHT('00'+CAST(ISNULL(datediff(second,dateadd(SECOND,-osi.ms_ticks/1000,GETDATE()),GETDATE()) % 3600 % 60, 0) AS VARCHAR(2)),2)
        )
from sys.dm_os_sys_info as osi;
"
set quoted_identifier on;

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql , @params, @perfmon_host_name;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql , @params, @perfmon_host_name;

