declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);

set @sql_instance = '$server';
set @perfmon_host_name = '$perfmon_host_name';
set @params = N'@perfmon_host_name varchar(255)';

set quoted_identifier off;
set @sql = "
if exists (select 1 from sys.dm_os_cluster_nodes) 
begin
	select NodeName, status_description as status, [current_role] = case when is_current_owner = 1 then 'Active' else 'Passive' end
	from sys.dm_os_cluster_nodes
end
else if exists (select 1 from sys.availability_groups)
begin
	select ar.replica_server_name, [current_role] = ars.role_desc, [sync_health] = ars.synchronization_health_desc, [ag_name] = ag.name
	from sys.availability_groups AS ag 
	JOIN sys.availability_replicas AS ar 
		ON ag.group_id = ar.group_id
	JOIN sys.dm_hadr_availability_replica_states AS ars
		ON ar.replica_id = ars.replica_id
end
else 
	select [Result] = 'HADR Not Configured'
"
set quoted_identifier on;

