declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);
declare @server_product_version varchar(20);
declare @server_major_version_number tinyint;
declare @server_minor_version_number smallint;

SET @server_product_version = '$product_version';
SET @server_major_version_number = $major_version_number;
SET @server_minor_version_number = $minor_version_number;

set @sql_instance = '$server';
set @perfmon_host_name = '$perfmon_host_name';
set @params = N'@server_product_version varchar(20), @server_major_version_number tinyint, @server_minor_version_number smallint';

set quoted_identifier off;
set @sql = "
;with t_current as
(
	select	top 1 [MajorVersionNumber]
			--,[@server_minor_version_number] = @server_minor_version_number
			,[MinorVersionNumber]
			,[Branch]
			,[Url]
			,[ReleaseDate]
			,[MainstreamSupportEndDate]
			,[ExtendedSupportEndDate]
			,[MajorVersionName]
			,[MinorVersionName]	  
	from [master].[dbo].[SqlServerVersions] as c
	where [MajorVersionNumber] = @server_major_version_number
	and [MinorVersionNumber] <= @server_minor_version_number
	order by [MinorVersionNumber] desc
)
,t_latest as 
(
	select	top 1 [MajorVersionNumber]
			--,[@server_minor_version_number] = @server_minor_version_number
			,[MinorVersionNumber]
			,[Branch]
			,[Url]
			,[ReleaseDate]
			,[MainstreamSupportEndDate]
			,[ExtendedSupportEndDate]
			,[MajorVersionName]
			,[MinorVersionName]	  
	from [master].[dbo].[SqlServerVersions] as c
	where [MajorVersionNumber] = @server_major_version_number
	order by [MinorVersionNumber] desc
)
select	[Product Version] = @server_product_version
		,[Major Version] = @server_major_version_number
		,[Minor Version] = @server_minor_version_number
		,[Patch/Update] = c.Branch ,c.ReleaseDate ,c.MainstreamSupportEndDate --,c.ExtendedSupportEndDate
		,[Is MS Supported] = case when c.MainstreamSupportEndDate < getdate() then convert(bit,0) else convert(bit,1) end
		,[Latest_Patch] = l.Branch
		,[Patch_Gap] = datediff(day,c.ReleaseDate,l.ReleaseDate)
		,[Url-Current] = REPLACE(c.[Url],'https://support.microsoft.com/en-us/help/','')
		,[Url-Latest] = REPLACE(l.[Url],'https://support.microsoft.com/en-us/help/','')
from t_current c, t_latest l;
"
set quoted_identifier on;

exec dbo.sp_executesql @sql, @params, @server_product_version, @server_major_version_number, @server_minor_version_number;

declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;

set @sql_instance = '$server';
set @perfmon_host_name = '$perfmon_host_name';
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
set @params = N'@perfmon_host_name varchar(255), @start_time_utc datetime2, @end_time_utc datetime2';

set quoted_identifier off;
set @sql = "
select pc.collection_time_utc as time, 
		case when (pc.object = 'processor' and pc.counter in ('% Processor Time') and pc.instance = '_total') then 'OS'
			when (pc.object = 'process' and pc.counter in ('% Processor Time') and pc.instance like 'sqlservr%') then 'Sql Server'
			else null
			end as metric,
	--object, counter as metric, instance
	case when pc.object = 'processor' then pc.value else pc.value/$sql_schedulers end as value
from $perfmon_table_name pc with (nolock)
where collection_time_utc between @start_time_utc and @end_time_utc
and host_name = @perfmon_host_name
and (   (pc.object = 'processor' and pc.counter in ('% Processor Time') and pc.instance = '_total')
	   or (pc.object = 'process' and pc.counter in ('% Processor Time') and (pc.instance = 'sqlservr' or pc.instance = 'sqlservr$$instance_name'))
	  )
and pc.value > 0
--order by time
"
set quoted_identifier on;

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql , @params, @perfmon_host_name, @start_time_utc, @end_time_utc;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql , @params, @perfmon_host_name, @start_time_utc, @end_time_utc;

declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;

set @sql_instance = '$server';
set @perfmon_host_name = '$perfmon_host_name';
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
set @params = N'@perfmon_host_name varchar(255), @start_time_utc datetime2, @end_time_utc datetime2';

set quoted_identifier off;
set @sql = "
set nocount on;

declare @collection_time datetime;
select @collection_time = max(l.collection_time) from dbo.WhoIsActive l;

-- Get Blocking Tree
if exists (select 1/0 from dbo.WhoIsActive w with (nolock) where w.collection_time = @collection_time and (blocking_session_id is not null or blocked_session_count > 0))
begin
	;WITH T_BLOCKERS AS (
		-- Find block Leaders
		SELECT	datediff(MILLISECOND, start_time, r.collection_time) as duration_ms, [collection_time], [session_id], 
				[sql_text] = REPLACE(REPLACE(REPLACE(REPLACE(CAST(COALESCE([sql_command],[sql_text]) AS VARCHAR(MAX)),char(13),''),CHAR(10),''),'<?query --',''),'--?>',''), 
				command = additional_info.value('(/additional_info/command_type)[1]','varchar(125)'), [login_name], wait_info, [blocking_session_id], [blocked_session_count] = replace(blocked_session_count,',',''),
				[status], open_tran_count, [host_name], [database_name], [program_name], tasks, granted_memory, r.CPU, r.used_memory, r.[tempdb_allocations], r.[tempdb_current], r.[reads], r.[writes], r.[physical_io], additional_info,
				[LEVEL] = CAST (REPLICATE ('0', 4-LEN (CAST (r.session_id AS VARCHAR))) + CAST (r.session_id AS VARCHAR) AS VARCHAR (1000))
				,[head_blocker] = session_id ,query_plan
		FROM	dbo.WhoIsActive AS r with (nolock) WHERE r.collection_time = @collection_time and (r.blocking_session_id IS NULL AND blocked_session_count > 0)
		--	
		UNION ALL
		--
		SELECT	datediff(MILLISECOND, r.start_time, r.collection_time) as duration_ms, r.[collection_time], r.[session_id], 
				[sql_text] = REPLACE(REPLACE(REPLACE(REPLACE(CAST(COALESCE(r.[sql_command],r.[sql_text]) AS VARCHAR(MAX)),char(13),''),CHAR(10),''),'<?query --',''),'--?>',''), 
				command = r.additional_info.value('(/additional_info/command_type)[1]','varchar(125)'), r.[login_name], r.wait_info, r.[blocking_session_id], [blocked_session_count] = replace(r.blocked_session_count,',',''),
				r.[status], r.open_tran_count, r.[host_name], r.[database_name], r.[program_name], r.tasks, r.granted_memory, r.CPU, r.used_memory, r.[tempdb_allocations], r.[tempdb_current], r.[reads], r.[writes], r.[physical_io], r.additional_info,
				CAST (B.LEVEL + RIGHT (CAST ((1000 + r.session_id) AS VARCHAR (100)), 4) AS VARCHAR (1000)) AS LEVEL
				,[head_blocker] = case when B.[head_blocker] is null then B.session_id else B.[head_blocker] end ,r.query_plan
		FROM	dbo.WhoIsActive AS r with (nolock)
		INNER JOIN  T_BLOCKERS AS B ON	r.collection_time = B.collection_time AND r.blocking_session_id = B.session_id
		WHERE	 r.blocking_session_id <> r.session_id
	)
	SELECT	[collection_time] = DATEADD(mi, DATEDIFF(mi, getdate(), getutcdate()), [collection_time]), 
			[dd hh:mm:ss.mss] = right('0000'+convert(varchar, duration_ms/86400000),2)+ ' '+convert(varchar,dateadd(MILLISECOND,duration_ms,'1900-01-01 00:00:00'),114),
			[session_id], 
			[BLOCKING_TREE] = N'    ' + REPLICATE (N'|         ', LEN (LEVEL)/4 - 1) 
							+	CASE	WHEN (LEN(LEVEL)/4 - 1) = 0
										THEN 'HEAD -  '
										ELSE '|------  ' 
								END
							+	CAST (r.session_id AS NVARCHAR (10)) + N' ' + ISNULL((CASE WHEN LEFT(ISNULL(r.[sql_text],''),1) = '(' THEN SUBSTRING(ISNULL(r.[sql_text],''),CHARINDEX('exec',ISNULL(r.[sql_text],'')),LEN(ISNULL(r.[sql_text],'')))  ELSE ISNULL(r.[sql_text],'') END),''),
			[blocking_session_id], [blocked_session_count] = case when [blocked_session_count] = 0 then null else [blocked_session_count] end, r.wait_info, [login_name], [program_name], [host_name], tasks, command, [database_name], status, r.open_tran_count, r.CPU, [used_memory_kb] = (r.used_memory*8.0), [reads_kb] = (r.[reads]*8.0), r.[writes], [tempdb_allocations_kb] = tempdb_allocations*8.0, tempdb_current_kb = tempdb_current*8.0, granted_memory_kb = granted_memory * 8.0
			--,r.query_plan ,r.additional_info
	FROM	T_BLOCKERS AS r ORDER BY collection_time, LEVEL;
end
else
  select 'No Blocking Found for Time Window' as [No Result]
"
set quoted_identifier on;

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql , @params, @perfmon_host_name, @start_time_utc, @end_time_utc;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql , @params, @perfmon_host_name, @start_time_utc, @end_time_utc;

declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;

set @sql_instance = '$server';
set @perfmon_host_name = '$perfmon_host_name';
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
set @params = N'@perfmon_host_name varchar(255), @start_time_utc datetime2, @end_time_utc datetime2';

set quoted_identifier off;
set @sql = "
set nocount on;
select DATEADD(mi, DATEDIFF(mi, getdate(), getutcdate()), collection_time) as [time], w.login_name as metric, sum(convert(int,replace(w.blocked_session_count,',',''))) as [value]
from $whoisactive_table_name w with (nolock)
where (collection_time between DATEADD(mi, DATEDIFF(mi, GETUTCDATE(), GETDATE()), @start_time_utc) and DATEADD(mi, DATEDIFF(mi, GETUTCDATE(), GETDATE()), @end_time_utc))
and (w.blocking_session_id is null and replace(w.blocked_session_count,',','') > 0) -- get lead blockers only
group by collection_time, w.login_name
order by time
"
set quoted_identifier on;

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql , @params, @perfmon_host_name, @start_time_utc, @end_time_utc;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql , @params, @perfmon_host_name, @start_time_utc, @end_time_utc;

declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);
declare @start_time_utc datetime2;
