set quoted_identifier off;
set @sql = "
select count(*) as blocked_counts --, max(wait_time)/1000 as wait_time_s
from sys.dm_exec_requests r with (nolock) 
where r.blocking_session_id <> 0
and wait_time >= ($blocked_threshold_seconds*1000) -- Over 60 seconds
"
set quoted_identifier on;

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql , @params, @perfmon_host_name;
